#include<bits/stdc++.h>
using namespace std;
#define N 205
typedef long long ll;
ll tong[N];
string s;
int main()
{
	freopen("number.in", "r", stdin);
	freopen("number.out", "w", stdout);
	cin >> s;
	ll len = s.length();
	for(ll i = 0; i < len; i++)
	{
		tong[(ll)s[i]]++;
	}
	bool flag = true;
	for(ll i = '9'; i >= '1'; i--)
	{
		for(ll j = 1; j <= tong[i]; j++)
		{
			flag = false;
			cout << (char)(i);
		}
	}
	if(flag)
	{
		cout << 0;
	}
	else
	{
		for(ll i = 1; i <= tong[48]; i++)
		{
			cout << 0;
		}
	}
	return 0;
}































































































































































