#include<bits/stdc++.h>
using namespace std;
#define N 205
typedef long long ll;
int main()
{
	freopen("xor.in", "r", stdin);
	freopen("xor.out", "w", stdout);
	ll n, tot = 0, t, k;
	cin >> n >> k;
	for(ll i = 1; i <= n; i++)
	{
		cin >> t;
		if(t)tot++;
	}
	if(k == 0)
	{
		cout << (tot / 2);
	}
	else
	{
		cout << tot;
	}
	return 0;
}
