#include<bits/stdc++.h>#define int long longconst int mod =  998244353;using namespace std;int n;int arr[5005];int ans;int dp[5005];int fa(int k){	dp[0] = 1;	if(k == 0){		return 1;	}	if(dp[k] != 0){		return dp[k];	}	dp[k] = k * fa(k - 1);	dp[k] %= mod;	return dp[k];}signed main(){	freopen("polygon.in","r",stdin);	freopen("polygon.out","w",stdout);	cin >> n;	int ans = 0;	for(int i = 3;i <= n;i++){		ans += fa(n) / fa(i) / fa(i);		ans %= mod;	}	cout << ans;}