#include<bits/stdc++.h>
using namespace std;
#define int long long
int n,m,ans;
const int MOD=998244353;
int p[1100],a[1100],ma[1100],cheng[1100];//ma:最大能到达的位置（这个位置不行） 
map<int,int>xuan;
int dfs(int now,int cnt,int lose)
{
	//cout<<"现在的位置："<<now<<" 已经有几个人成功了："<<cnt<<" 有几个人无了"<<lose<<endl;
	//for(auto i:xuan)cout<<i.second<<" ";
	//cout<<endl;
	//cout<<ans<<endl;
	//cout<<now<<" "<<cnt<<" "<<lose<<endl;
	if(now==n+1)
	{
		if(cnt>=m)ans++;
		return 0;
	}
	if(cnt>=m)
	{
		ans=(ans+cheng[n-now+1])%MOD;
		return 0;
	}
	for(int i=1;i<=n;i++)
	{
		if(xuan[i]==0)
		{
			xuan[i]=1;
			if(p[now]==0)dfs(now+1,cnt,lose+1);
			else if(lose>=a[i])dfs(now+1,cnt,lose+1);
			else 
			{
				dfs(now+1,cnt+1,lose);
			}
			xuan[i]=0;
		}
	}
	return 0;
}
signed main()
{
	freopen("employ.txt","r",stdin);
	freopen("employ.out","w",stdout);
	cin>>n>>m;
	//cout<<n<<m;
	int cc=1;
	cheng[0]=1;
	for(int i=1;i<=n;i++)
	{
		cheng[i]=(cheng[i-1]*cc)%MOD;
		cc++; 
	}
	memset(ma,0x7f,sizeof(ma));
	char c;
	for(int i=1;i<=n;i++)cin>>c,p[i]=c-'0';
	int nnn=n;
	for(int i=1;i<=n;i++)cin>>a[i];
	for(int i=1;i<=n+1;i++)
	{
		if(i==n+1)
		{
			return cout<<cheng[nnn],0;
		}
		if(a[i]==0)break;
		if(p[i]==0)break;
	}
	int shu=0; 
	for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=n;j++)
		{
			if(shu>=a[j])ma[j]=min(ma[j],i);
		}
		if(a[i]==0)shu++;
	}
	dfs(1,0,0);
	cout<<ans;
}

