bool check(vector<int> s,int m){
	int tot=0,maxn=-99999999,len=s.size();
	for(int i=1;i<=len;i++){
		tot+=s[i];
		maxn=max(maxn,s[i]);
	}
	if(m<3||!(tot>2*maxn)){
		return false;
	}
	return true;
}



