long long a(int q)
{
	if(q<=0)
	{
		return 1;
	}
	if(mem[q])
	{
		return mem[q];
	}
	return mem[q]=(q*a(q-1))%mod;
}

void f(int d,int w,int o,int p)
{
	if(w>=m)
	{
		ans=(ans+mem[n-d+p])%mod;
		//cout<<"ans+=c "<<n-d<<endl;
		return ;
	}
	if(d>n)
	{
		return ;
		
	}
	if(s[d]=='0')
	{
		f(d+1,w,o+1,p+1);
		return ;
	}
	for(int x=0;x<n;x++)
	{
		if(v[x])
		{
			continue;
		}
		v[x]=1;
		if(o<c[x])
		{
			
			f(d+1,w+1,o,p);
		}
		else
		{
			f(d+1,w,o+1,p);
		}
		v[x]=0;
	}
}
