#include<bits/stdc++.h>
using namespace std;
const int N=5e3+10,mod=998244353;
long long n,a[N],ans,ma;
string b="";
set<string> st;
void dfs(int x,long long m,long long k,int p){
    if(x>n) return ;
    if(k>2*m&&p>=3){
        st.insert(b);
    }
    b[x+1]='1';
    dfs(x+1,max(m,a[x+1]),k+a[x+1],p+1);
    b[x+1]='0';
    dfs(x+1,m,k,p);
}
int main(){
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    cout.tie(nullptr);
    freopen("polygon.in","r",stdin);
    freopen("polygon.out","w",stdout);
    cin>>n;
    for(int i=1;i<=n;i++){
        cin>>a[i];
        ma=max(a[i],ma);
    }
    if(ma<=1){
        cout<<(1LL*n*(n-1)*(n-2)/6)%mod;
        return 0;
    }
    dfs(1,0,0,0);
    b[1]='1';
    dfs(1,a[1],a[1],1);
    b[1]='0';
    cout<<st.size()%mod;
    return 0;
}
