#include <iostream>
#include <stdio.h>
#include <algorithm>
#include <string>
using namespace::std;
const int N = 2e5 + 10;
int n, q;
pair<string, string> str[N];
pair<string, string> questions[N];
int main()
{
    freopen("replace.in", "r", stdin);
    freopen("replace.out", "w", stdout);
    scanf("%d%d", &n, &q);
    for(int i = 1; i <= n; i++) {
        cin >> str[i].first >> str[i].second;
    }
    for(int i = 1; i <= q; i++)
    {
        cin >> questions[i].first >> questions[i].second;
    }
    for(int i = 1; i <= q; i++) {
        printf("0\n");
    }
    fclose(stdin);
    fclose(stdout);
    return 0;
}

































