#include<bits/stdc++.h>
using namespace std;
long long jz[6000];
long long jie(int n){
	if (n==1 || n==0){
		return 1;
	}
	if (jz[n-1]!=0){
		jz[n]=jz[n-1]*n;
		return jz[n];
	}
	jz[n]=n*jie(n-1);
	return jz[n];
}
long long zh(int n,int m){
	cout<<n<<" "<<m<<endl;
	return jie(m)/(jie(n)*jie(abs(m-n)));
}

