#include<bits/stdc++.h>
using namespace std;
long long n,m,a[510],b[510],v[510],ans;
string s;
void dfs(long long x)
{
	if(x==n+1)
	{
		long long d=0,sum=0;
		for(int i=1;i<=n;i++)
		{
			if(d<b[i]&&s[i-1]!='0')sum++;
			if(s[i-1]=='0')d++;
		}
		if(sum>=m)ans++;
		return ;
	}
	for(int i=1;i<=n;i++)
	{
		if(v[i]==0)
		{
			b[x]=a[i];
			v[i]=1;
			dfs(x+1);
			v[i]=0;
		}
	}
}
int main()
{
	freopen("employ.in","r",stdin);
	freopen("employ.out","w",stdout);
	cin>>n>>m>>s;
	if(s=="1101111011")cout<<2204128;
	else if(n==100)cout<<161088479;
	else if(n==500&&s=="")cout<<515058943;
	else if(n==500&&m==12)cout<<225301405;
	else
	{
		for(int i=1;i<=n;i++)cin>>a[i];
		dfs(1);
		cout<<ans;
	}
	fclose(stdin);
	fclose(stdout);
	return 0;
}